import React, { useState, useEffect } from "react";
import axios from "axios";

const Users = () => {
 const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [newUser, setNewUser] = useState({ username: "", email: "" }); // State for new user data
  useEffect(() => {
    const fetchUsers = async () => {
      setIsLoading(true); // Set loading state to true
      try {
        const response = await axios.get("http://127.0.0.1:8000/users/");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching users:", error);
        setError("Error fetching users. Please try again later.");
      } finally {
        setIsLoading(false); // Set loading state to false after request completes
      }
    };

    fetchUsers();
  }, []);
  const handleCreateUser = (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    const updatedData = [...data, newUser]; // Create a copy of data array and add new user
    setData(updatedData);
    setNewUser({ username: "", email: "" }); // Reset new user form
  };


 // ... existing code in Users component

 return (
   <div>
      <h1>API Response (Simulated CRUD)</h1>
      {isLoading ? (
        <p>Loading users...</p> // Display loading message while fetching
      ) : error ? (
        <p className="error">{error}</p> // Display error message with styling
      ) : data ? (
        <div>
          <ul>
            {/* ... existing user list rendering logic */}
          </ul>
          <h2>Create User</h2>
          <form onSubmit={handleCreateUser}>
            {/* Form fields for username, email, etc. */}
            <button type="submit">Create User</button>
          </form>
        </div>
      ) : (
        <p>No users found.</p> // Handle empty data scenario
      )}
    </div>
  );
};


export default Users;
