import React, { useState, useEffect } from "react";
import axios from "axios";

const TweetComposer = () => {
  const [tweetContent, setTweetContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/tweets/");
      setTweets(response.data);
    } catch (error) {
      console.error("Error fetching tweets:", error);
    }
  };

  const handleInputChange = (e) => {
    setTweetContent(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Handling token might need more logic, for now, it's commented out
      // const token = localStorage.getItem("token");
      // handleToken(token);

      console.log(tweetContent);
      const response = await axios.post('http://127.0.0.1:8000/tweets/', {
        content: tweetContent,
        user: 1, // Assuming the user ID is 1 for now
        is_liked: false,
        comment: 'hello'
      });
      console.log("Tweet created:", response.data);
      setTweets([...tweets, response.data]); // Add newly created tweet to the list
    } catch (error) {
      console.error("Error creating tweet:", error);
      setError("An error occurred while creating the tweet.");
    } finally {
      setLoading(false);
      setTweetContent("");
    }
  };

  const handleLike = async (tweetId) => {
    try {
      const response = await axios.post(`http://127.0.0.1:8000/tweets/${tweetId}/like/`);
      console.log("Tweet liked:", response.data);
      // Update the like count in the UI
      setTweets((prevTweets) => {
        return prevTweets.map((tweet) => {
          if (tweet.id === tweetId) {
            return {
              ...tweet,
              is_liked: true,
              like_count: tweet.like_count + 1
            };
          }
          return tweet;
        });
      });
    } catch (error) {
      console.error("Error liking tweet:", error);
      setError("An error occurred while liking the tweet.");
    }
  };

  const handleDislike = async (tweetId) => {
    try {
      const response = await axios.post(`http://127.0.0.1:8000/tweets/${tweetId}/dislike/`);
      console.log("Tweet disliked:", response.data);
      // Update the dislike count in the UI
      setTweets((prevTweets) => {
        return prevTweets.map((tweet) => {
          if (tweet.id === tweetId) {
            return {
              ...tweet,
              is_liked: false,
              like_count: tweet.like_count > 0 ? tweet.like_count - 1 : 0
            };
          }
          return tweet;
        });
      });
    } catch (error) {
      console.error("Error disliking tweet:", error);
      setError("An error occurred while disliking the tweet.");
    }
  };

  return (
    <div className="tweet">
      <h2>Compose a Tweet</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={tweetContent}
          onChange={handleInputChange}
          placeholder="What's happening?"
          rows={4}
          cols={50}
          disabled={loading}
        />
        <button className="post" type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post"}
        </button>
      </form>
      {error && <p>{error}</p>}
      <h2>Tweets</h2>
      <ul>
        {tweets.map((tweet) => (
          <li key={tweet.id}>
            <div>
              <span>{tweet.content}</span>
              <button onClick={() => handleLike(tweet.id)} disabled={tweet.is_liked}>
                Like ({tweet.like_count})
              </button>
              <button onClick={() => handleDislike(tweet.id)} disabled={!tweet.is_liked}>
                Dislike
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TweetComposer;
