import React, { useState, useEffect } from "react";
import axios from "axios";

const TweetComposer = () => {
  const [tweetContent, setTweetContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/tweets/");
      setTweets(response.data);
    } catch (error) {
      console.error("Error fetching tweets:", error);
    }
  };

  const handleInputChange = (e) => {
    setTweetContent(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post("http://127.0.0.1:8000/tweets/", {
        content: tweetContent,
      });
      console.log("Tweet created:", response.data);
      setTweets([...tweets, response.data]); // Add newly created tweet to the list
    } catch (error) {
      console.error("Error creating tweet:", error);
      setError("An error occurred while creating the tweet.");
    } finally {
      setLoading(false);
      setTweetContent("");
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/tweets/${id}/`);
      setTweets(tweets.filter(tweet => tweet.id !== id)); // Remove the deleted tweet from the list
    } catch (error) {
      console.error("Error deleting tweet:", error);
    }
  };

  const handleEdit = async (id, updatedContent) => {
    try {
      const response = await axios.put(`http://127.0.0.1:8000/tweets/${id}/`, {
        content: updatedContent,
      });
      const updatedTweetIndex = tweets.findIndex(tweet => tweet.id === id);
      const updatedTweets = [...tweets];
      updatedTweets[updatedTweetIndex] = response.data;
      setTweets(updatedTweets);
    } catch (error) {
      console.error("Error updating tweet:", error);
    }
  };

  return (
    <div>
      <h2>Compose a Tweet</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={tweetContent}
          onChange={handleInputChange}
          placeholder="What's happening?"
          rows={4}
          cols={50}
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post"}
        </button>
      </form>
      {error && <p>{error}</p>}
      <h2>Tweets</h2>
      <ul>
        {tweets.map((tweet) => (
          <li key={tweet.id}>
            <div>
              <span>{tweet.content}</span>
              <button onClick={() => handleDelete(tweet.id)}>Delete</button>
              <button onClick={() => {
                const updatedContent = prompt("Enter the updated content:", tweet.content);
                if (updatedContent !== null) {
                  handleEdit(tweet.id, updatedContent);
                }
              }}>Edit</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TweetComposer;
