import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch all users
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/users/');
        const userList = response.data;
        setUsers(userList);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleInputChange = (userId, fieldName, value) => {
    // Find the user with the given userId and update the specific field
    const updatedUsers = users.map(user => {
      if (user.id === userId) {
        return { ...user, [fieldName]: value };
      }
      return user;
    });
    setUsers(updatedUsers);
  };

  const handleSaveChanges = async (userId, updatedUserData) => {
    try {
      const response = await axios.put(`http://127.0.0.1:8000/users/${userId}/`, updatedUserData);
      if (response.status === 200) {
        console.log('User updated successfully');
      } else {
        console.log('Failed to update user');
      }
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div className='main_top'>
      <button a/>
      <h1>All Users</h1>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            <div>
              <strong>Username:</strong>
              <input
                type="text"
                value={user.username}
                onChange={e => handleInputChange(user.id, 'username', e.target.value)}
              />
            </div>
            <div>
              <strong>Email:</strong>
              <input
                type="email"
                value={user.email}
                onChange={e => handleInputChange(user.id, 'email', e.target.value)}
              />
            </div>
            <div>
              <strong>Password:</strong>
              <input
                type="password"
                value={user.password}
                onChange={e => handleInputChange(user.id, 'password', e.target.value)}
              />
            </div>
            <div>
              <strong>User ID:</strong> {user.id}
            </div>
            <div>
              <strong>Staff:</strong> {user.staff}
            </div>
            {/* Save changes button */}
            <button onClick={() => handleSaveChanges(user.id, user)}>Save Changes</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
