import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { redirect,useHistory } from 'react-router-dom';


const UserList = () => {
  const [users, setUsers] = useState([]);
  const history = useHistory();

  useEffect(() => {
    // Fetch all users
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/users/');
        const userList = response.data;
        setUsers(userList);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleDelete = async userId => {
    try {
      await axios.delete(`http://127.0.0.1:8000/users/${userId}`);
      // Remove the deleted user from the state
      setUsers(users.filter(user => user.id !== userId));
      // Optionally, you can show a success message
    } catch (error) {
      console.error('Error deleting user:', error);
      // Optionally, you can show an error message
    }
  };
  const handleEdit = userId => {
   history.push(`/edit/${userId}`);
    // Here you can implement the logic to navigate to the edit page
    // or display an edit form for the selected user
  };

  return (
    <div>
      <h1>User List</h1>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            <div>
              <strong>Username:</strong> {user.username}
            </div>
            <div>
              <strong>Email:</strong> {user.email}
            </div>
            {/* Edit and Delete buttons */}
            <button onClick={() => handleDelete(user.id)}>Delete</button>
            <button onClick={() => handleEdit("Edit functionality here")}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
