import React, { useState, useEffect } from "react";
import axios from "axios";

const TweetComposer = ({ username }) => { // Receive username as a prop
  const [tweetContent, setTweetContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/tweets/");
      setTweets(response.data);
    } catch (error) {
      console.error("Error fetching tweets:", error);
    }
  };

  const handleInputChange = (e) => {
    setTweetContent(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(`http://127.0.0.1:8000/tweets/${username}/`, {
        content: tweetContent,
      });
      console.log("Tweet created:", response.data);
      setTweets([...tweets, response.data]); // Add newly created tweet to the list
    } catch (error) {
      console.error("Error creating tweet:", error);
      setError("An error occurred while creating the tweet.");
    } finally {
      setLoading(false);
      setTweetContent("");
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/tweets/${id}/`);
      setTweets(tweets.filter(tweet => tweet.id !== id)); // Remove the deleted tweet from the list
    } catch (error) {
      console.error("Error deleting tweet:", error);
    }
  };

  const handleLike = async (id) => {
    try {
      await axios.put(`http://127.0.0.1:8000/tweets/${id}/like/`);
      const updatedTweets = tweets.map(tweet => {
        if (tweet.id === id) {
          return { ...tweet, is_liked: !tweet.is_liked };
        }
        return tweet;
      });
      setTweets(updatedTweets);
    } catch (error) {
      console.error("Error liking tweet:", error);
    }
  };

  const handleFollow = async (userId) => {
    try {
      // Implement follow functionality
      // You need to send a request to your backend to follow the user with the given userId
    } catch (error) {
      console.error("Error following user:", error);
    }
  };

  const handleComment = async (id) => {
    try {
      // Implement comment functionality
      // You need to navigate to a separate page or modal to add comments to the tweet with the given id
    } catch (error) {
      console.error("Error commenting on tweet:", error);
    }
  };

  return (
    <div className="tweet">
      <h2>Compose a Tweet</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={tweetContent}
          onChange={handleInputChange}
          placeholder="What's happening?"
          rows={4}
          cols={50}
          disabled={loading}
        />
        <button className="post" type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post"}
        </button>
      </form>
      {error && <p>{error}</p>}
      <h2>Tweets</h2>
      <ul>
        {tweets.map((tweet) => (
          <li key={tweet.id}>
            <div>
              <span>{tweet.content}</span>
              <button onClick={() => handleDelete(tweet.id)}>Delete</button>
              <button onClick={() => handleLike(tweet.id)}>{tweet.is_liked ? 'Unlike' : 'Like'}</button>
              <button onClick={() => handleFollow(tweet.user.id)}>Follow</button>
              <button onClick={() => handleComment(tweet.id)}>Comment</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TweetComposer;
