import React, { useState, useEffect } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";

const TweetComposer = () => {
  const [tweetContent, setTweetContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/tweets/");
      // Ensure that likes and dislikes have initial default values of 0
      const tweetsWithDefaults = response.data.map(tweet => ({
        ...tweet,
        likes: tweet.likes || 0,
        dislikes: tweet.dislikes || 0
      }));
      setTweets(tweetsWithDefaults);
    } catch (error) {
      console.error("Error fetching tweets:", error);
    }
  };

  const handleInputChange = (e) => {
    setTweetContent(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const handleToken = () => {
        const token = localStorage.getItem("token");

        if (!token) {
          console.error("Token not found");
          return;
        }

        try {
          const decodedToken = jwtDecode(token);
          console.log("Decoded token:", decodedToken);
        } catch (error) {
          console.error("Error decoding token:", error);
        }
      };

      // handleToken();
      console.log(tweetContent);
      const response = await axios({
        method: 'post',
        url: 'http://127.0.0.1:8000/tweets/',
        data: {
          content: tweetContent,
          id: 5,
          is_liked: false,
          comment: 'hello'
        }
      });
      console.log("Tweet created:", response.data);
      setTweets([...tweets, { ...response.data, likes: 0, dislikes: 0 }]); // Add newly created tweet to the list with initial default values for likes and dislikes
    } catch (error) {
      console.error("Error creating tweet:", error);
      setError("An error occurred while creating the tweet.");
    } finally {
      setLoading(false);
      setTweetContent("");
    }
  };

  const handleLikeButtonClick = (tweetId) => {
    const updatedTweets = tweets.map(tweet => {
      if (tweet.id === tweetId) {
        return { ...tweet, isLiked: !tweet.isLiked, likes: tweet.likes + 1 };
      }
      return tweet;
    });
    setTweets(updatedTweets);
  };

  const handleDislikeButtonClick = (tweetId) => {
    const updatedTweets = tweets.map(tweet => {
      if (tweet.id === tweetId) {
        return { ...tweet, isDisliked: !tweet.isDisliked, dislikes: tweet.isDisliked ? tweet.dislikes - 1 : tweet.dislikes + 1 };
      }
      return tweet;
    });
    setTweets(updatedTweets);
  };

  return (
    <div className="tweet">
      <h2>Compose a Tweet</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={tweetContent}
          onChange={handleInputChange}
          placeholder="What's happening?"
          rows={4}
          cols={50}
          disabled={loading}
        />
        <button className="post" type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post"}
        </button>
      </form>
      {error && <p>{error}</p>}
      <h2>Tweets</h2>
      <ul>
        {tweets.map((tweet) => (
          <li key={tweet.id}>
            <div>
              <span>{tweet.content}</span>
              <button className={tweet.isLiked ? "text-primary" : ""} onClick={() => handleLikeButtonClick(tweet.id)}>
                <i className="fa fa-thumbs-up">{tweet.likes}</i>
              </button>
              <button className={tweet.isDisliked ? "text-primary" : ""} onClick={() => handleDislikeButtonClick(tweet.id)}>
                <i className="fa fa-thumbs-down">{tweet.dislikes}</i>
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TweetComposer;
