import React, { useState, useEffect } from "react";
import axios from "axios";

const Users = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [newUser, setNewUser] = useState({
    username: "",
    email: "",
    password: "", // Assuming password field is required
    is_staff: false, // Assuming boolean values for is_staff and is_active
    is_active: false,
  });

  useEffect(() => {
    const fetchUsers = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get("http://127.0.0.1:8000/users/");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching users:", error);
        setError("Error fetching users. Please try again later.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchUsers();
  }, []);

  const updateUser = async (userId, updatedUserData) => {
    try {
      const response = await axios.put(`http://127.0.0.1:8000/users/${userId}`, updatedUserData);
      const updatedUser = response.data;
      setData(data.map(user => user.user_id === updatedUser.user_id ? updatedUser : user));
      console.log(`User with ID ${userId} updated:`, updatedUser);
    } catch (error) {
      console.error(`Error updating user with ID ${userId}:`, error);
    }
  };

  const deleteUser = async (userId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/users/${userId}`);
      setData(data.filter(user => user.user_id !== userId));
      console.log(`Delete user with ID ${userId}`);
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleCreateUser = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    try {
      const response = await axios.post("http://127.0.0.1:8000/users/", newUser); // Assuming POST request for creation
      const createdUser = response.data;
      setData([...data, createdUser]); 
      setNewUser({ username: "", email: "", password: "" });
    } catch (error) {
      console.error("Error creating user:", error);
      setError("Error creating user. Please try again later.");
    }
  };

  return (
    <div>
      <h1>Existing Users</h1>
      {isLoading ? (
        <p>Loading users...</p> // Display loading message while fetching
      ) : error ? (
        <p className="error">{error}</p> // Display error message with styling
      ) : data ? (
        <div>
          <ul>
            {data.map((user) => (
              <li key={user.user_id}>
                {user.username} - {user.email}
                <button onClick={() => updateUser(user.user_id, { username: 'New Username', email: 'newemail@example.com' })}>Update user</button>
                <button onClick={() => deleteUser(user.user_id)}>Delete user</button>
              </li>
            ))}
          </ul>
          <h2>Create User</h2>
          <form onSubmit={handleCreateUser}>
            {/* Input fields for creating a new user */}
          </form>
        </div>
      ) : null}
    </div>
  );
};

export default Users;
