import React, { useState } from "react";
import axios from "axios";

const TweetComposer = () => {
  const [tweetContent, setTweetContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    setTweetContent(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Make a POST request to your backend API to create the tweet
      const response = await axios.post("tweets/", {
        content: tweetContent,
      });
      console.log("Tweet created:", response.data);
      // Optionally, you can show a success message or redirect the user
    } catch (error) {
      console.error("Error creating tweet:", error);
      setError("An error occurred while creating the tweet.");
    } finally {
      setLoading(false);
      setTweetContent("");
    }
  };

  return (
    <div>
      <h2>Compose a Tweet</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={tweetContent}
          onChange={handleInputChange}
          placeholder="What's happening?"
          rows={4}
          cols={50}
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post"}
        </button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default TweetComposer;
