import React, { useState, useEffect } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode"; // Adjust the import statement

const TweetComposer = () => {
  const [tweetContent, setTweetContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/tweets/");
      setTweets(response.data);
    } catch (error) {
      console.error("Error fetching tweets:", error);
    }
  };

  const handleInputChange = (e) => {
    setTweetContent(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem("token");
      console.log("Token:", token); // Log the token to verify its format and contents
      const decodedToken = jwtDecode(token);
      const id = decodedToken.id;

      const response = await axios.post(`http://127.0.0.1:8000/tweets/${id}/`, {
        content: tweetContent,
      });
      console.log("Tweet created:", response.data);
      setTweets([...tweets, response.data]); // Add newly created tweet to the list
    } catch (error) {
      console.error("Error creating tweet:", error);
      setError("An error occurred while creating the tweet.");
    } finally {
      setLoading(false);
      setTweetContent("");
    }
  };

  // Other functions remain unchanged

  return (
    <div className="tweet">
      <h2>Compose a Tweet</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={tweetContent}
          onChange={handleInputChange}
          placeholder="What's happening?"
          rows={4}
          cols={50}
          disabled={loading}
        />
        <button className="post" type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post"}
        </button>
      </form>
      {error && <p>{error}</p>}
      <h2>Tweets</h2>
      <ul>
        {tweets.map((tweet) => (
          <li key={tweet.id}>
            <div>
              <span>{tweet.content}</span>
              {/* Buttons and handlers */}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TweetComposer;
