import React, { useState, useEffect } from "react";
import axios from "axios";

const Users = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [newUser, setNewUser] = useState({
    username: "",
    email: "",
    password: "", // Assuming password field is required
    is_staff: false, // Assuming boolean values for is_staff and is_active
    is_active: false,
  });

  // Fetch users on component mount
  useEffect(() => {
    const fetchUsers = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get("http://127.0.0.1:8000/users/");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching users:", error);
        setError("Error fetching users. Please try again later.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // Handle creating a new user
  const handleCreateUser = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    // Add validation logic here (optional)

    try {
      const response = await axios.post("http://127.0.0.1:8000/users/", newUser); // Assuming POST request for creation
      const createdUser = response.data; // Handle response from backend
      // Update the UI to reflect the newly created user
      setData([...data, createdUser]); // Add new user to data array
      setNewUser({ username: "", email: "", password: "" }); // Reset new user form
    } catch (error) {
      console.error("Error creating user:", error);
      setError("Error creating user. Please try again later.");
    }
  };

  return (
    <div>
      <h1>Existing Users</h1>
      {isLoading ? (
        <p>Loading users...</p> // Display loading message while fetching
      ) : error ? (
        <p className="error">{error}</p> // Display error message with styling
      ) : data ? (
        <div>
          <ul>
            {data.map((user) => (
              <li key={user.user_id}>
                {user.username} - {user.email}
                <button >Update user</button>
                <></>
              </li>
            ))}
          </ul>
          <h2>Create User</h2>
          <form onSubmit={handleCreateUser}>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              name="username"
              value={newUser.username}
              onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
              required // Add validation for required fields
            />
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={newUser.email}
              onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
              required // Add validation for required fields
            />
            <label htmlFor="password">Password:</label>
            <input
              type="password" // Use password type for security
              id="password"
              name="password"
              value={newUser.password}
              onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
              required // Add validation for required fields
            />
            <label>
              <input
                type="checkbox"
                id="is_staff"
                name="is_staff"
                checked={newUser.is_staff}
                onChange={(e) => setNewUser({ ...newUser, is_staff: e.target.checked })}
              />
              Is Staff
            </label>
            <label>
              <input
                type="checkbox"
                id="is_active"
                name="is_active"
                checked={newUser.is_active}
                onChange={(e) => setNewUser({ ...newUser, is_active: e.target.checked })}
              />
              Is Active
            </label>
            <button type="submit">Create User</button>
          </form>
        </div>
      ) : null}
    </div>
  );
};

export default Users;
