import React, { useState } from 'react';
import axios from 'axios';

const EditUser = () => {
  const [formData, setFormData] = useState({
    username: '',
    userId: '', 
    email: '',
    password: '',
  });
  const [userData, setUserData] = useState(null); 

  const handleChange = event => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  };

  const handleSubmit = async event => {
    event.preventDefault();
    const response = await axios.get(`http://127.0.0.1:8000/users/${formData.userId}/`);
    setUserData(response.data[0]);
  };

  const handleEdit = async () => {
    try {
        console.log('User data:', userData);

        if (!userData.id) {
            console.error('No user data available to delete');
            return;
        }  
        const response = await axios.put(`http://127.0.0.1:8000/users/${foruserData.id}/`);
        if (response.status === 204) {
            console.log('User deleted successfully');
        } else {
            console.log('Failed to delete user');
        }
    } catch (error) {
        console.error('Error deleting user:', error);
    }
};

  return (
    <div className='main_top'>
      <button className='box_ grey' type="submit"><a href='/createuser'>Back</a></button>
      <h1>Delete User</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="userId">User ID:</label>
          <input
            type="text"
            id="userId"
            name="userId"
            value={formData.userId}
            onChange={handleChange}
          />
        </div>
        <button className='box_ grey' type="submit">Fetch User Info</button>
      </form>

      {userData && (
        <div>
          <h2>User Info</h2>
          <div>
            <strong>ID:</strong> {userData.id}
          </div>
          <div>
            <strong>Username:</strong> {userData.username}
          </div>
          <div>
            <strong>Email:</strong> {userData.email}
          </div>
          <div>
            <strong>Password:</strong> {userData.password}
          </div>
          <button className='box_ grey' onClick={handleEdit}>Edit User</button>
        </div>
      )}
    </div>
  );
};

export default EditUser;
