import React, { useState } from 'react';
import axios from 'axios';

const EditUser = () => {
  const [formData, setFormData] = useState({
    id: '',
    username: '',
    email: '',
    password: '',
  });
  const [userData, setUserData] = useState(null);

  const handleChange = event => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  };

  const handleSubmit = async event => {
    event.preventDefault();
    try {
      const response = await axios.get(`http://127.0.0.1:8000/users/${formData.id}/`);
      setUserData(response.data
        // [0]
        );
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  const handleDelete = async () => {
    try {
      if (!userData) {
        console.error('No user data available to delete');
        return;
      }
      const response = await axios.delete(`http://127.0.0.1:8000/users/${userData.id}/`);
      if (response.status === 204) {
        console.log('User deleted successfully');
        setUserData(null); // Clear user data after deletion
        setFormData({
          id: '',
          username: '',
          email: '',
          password: '',
        });
      } else {
        console.log('Failed to delete user');
      }
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleEdit = async () => {
    try {
      const response = await axios.put(`http://127.0.0.1:8000/users/${userData.id}/`, formData);
      console.log(response.data);
    } catch (error) {
      console.error('Error editing user:', error);
    }
  };

  return (
    <div className='main_top'>
      <button className='box_ grey' type="submit"><a href='/createuser'>Back</a></button>
      <h1>Edit User</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="id">User ID:</label>
          <input
            type="text"
            id="id"
            name="id"
            value={formData.id}
            onChange={handleChange}
          />
        </div>
        <button className='box_ grey' type="submit">Fetch User Info</button>
      </form>

      {userData && (
        <div>
          <h2>User Info</h2>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username || userData.username}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email || userData.email}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password || userData.password}
              onChange={handleChange}
            />
          </div>
          <button className='box_ grey' onClick={handleEdit}>Save Changes</button>
          <button className='box_ grey' onClick={handleDelete}>Delete User</button>
        </div>
      )}
    </div>
  );
};

export default EditUser;
