import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch all users
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/users/');
        const userList = response.data;
        setUsers(userList);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handlePut = async (userId, updatedUserData) => {
    try {
      const response = await axios.put(`http://127.0.0.1:8000/users/${userId}/`, updatedUserData);
      if (response.status === 200) {
        console.log('User updated successfully');
        // Update the user list after update
        const updatedUsers = users.map(user => user.id === userId ? { ...user, ...updatedUserData } : user);
        setUsers(updatedUsers);
      } else {
        console.log('Failed to update user');
      }
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div className='main_top'>
      <h1>All Users</h1>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            <div>
              <strong>Username:</strong>#need hrere field for edit {user.username}
            </div>
            <div>
              <strong>Email:</strong>#need hrere field for edit{user.email}
            </div>
            <div>
              <strong>Password:</strong>#need hrere field for edit {user.password}
            </div>
            <div>
              <strong>User ID:</strong>#need hrere field for edit {user.id}
            </div>
            <div>
              <strong>Staff:</strong> {user.staff}
            </div>
            {/* Edit and Delete buttons */}
            <button className='box_ grey' onClick={() => handlePut(user.id, { staff: !user.staff })}>Toggle Staff</button>
            <button><a href='/edituser'>Edit</a></button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
