import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EditUser = ({ userId }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  useEffect(() => {
    // Fetch user data based on userId
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/users/${userId}`);
        const userData = response.data;
        setFormData(userData);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    fetchUser();
  }, [userId]);

  const handleChange = event => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  };

  const handleSubmit = async event => {
    event.preventDefault();
    try {
      const response = await axios.put(`http://127.0.0.1:8000/users/${userId}`, formData);
      console.log(response.data);
      // Optionally, you can redirect the user to another page or show a success message
    } catch (error) {
      console.error('Error editing user:', error);
      // Optionally, you can show an error message to the user
    }
  };

  return (
    <div>
      <button className='box_ grey' type="submit"><a href='/createuser'> Back</a></button>
      <h1>Edit User</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </div>
        <button className='box_ grey' type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditUser;
