import React, { useState, useEffect } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";


const TweetComposer = () => {
  const [tweetContent, setTweetContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tweets, setTweets] = useState([]);

  const[like, setlike] = useState(0),
  [islike,setIsLike] = useState(false),
  onLikeButtonClick= () =>{
  setlike(like +(islike
    // ? -1 :1
    ));
  setIsLike(!islike);
  console.log("Liked")
  }
  const[Dislike, setDislike] = useState(0),
  [isDislike,setIsDIsLike] = useState(false),
  onDisLikeButtonClick= (Itemid) =>{
    setDislike(Dislike +(isDislike));
    setIsDIsLike(!isDislike);

    if (itemIndex !== -1) {
      updatedItems[itemIndex] = {
        ...updatedItems[itemIndex],
        dislikeCount: updatedItems[itemIndex].dislikeCount + (updatedItems[itemIndex].isDisliked ? 0 : 1),
        isDisliked: !updatedItems[itemIndex].isDisliked,
      };
    }
    console.log("Disliked")
  }


  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/tweets/");
      setTweets(response.data);
    } catch (error) {
      console.error("Error fetching tweets:", error);
    }
  };

  const handleInputChange = (e) => {
    setTweetContent(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const handleToken = () => {
        const token = localStorage.getItem("token");

        if (!token) {
          console.error("Token not found");
          return;
        }

        try {
          const decodedToken = jwtDecode(token);
          console.log("Decoded token:", decodedToken);
        } catch (error) {
          console.error("Error decoding token:", error);
        }
      };

      // handleToken();
      console.log(tweetContent);
      const response = await axios({
        method: 'post',
        url: 'http://127.0.0.1:8000/tweets/',
        data: {
          content: tweetContent,
          user: 1,
          is_liked: false,
          comment: 'hello'
        }
      });
      console.log("Tweet created:", response.data);
      setTweets([...tweets, response.data]); // Add newly created tweet to the list
    } catch (error) {
      console.error("Error creating tweet:", error);
      setError("An error occurred while creating the tweet.");
    } finally {
      setLoading(false);
      setTweetContent("");
    }
  };
  // const handleDislike = (tweetId) => {
  //   setDislikes(dislikes + 1);
  // };

  return (
    <div className="tweet">
      <h2>Compose a Tweet</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={tweetContent}
          onChange={handleInputChange}
          placeholder="What's happening?"
          rows={4}
          cols={50}
          disabled={loading}
        />
        <button className="post" type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post"}
        </button>
      </form>
      {error && <p>{error}</p>}
      <h2>Tweets</h2>
      <ul>
        {tweets.map((tweet) => (
          <li key={tweet.id}>
            <div>
              <span>{tweet.content}</span>
              <button className={""+(islike?"text-primary":"")}>
              <i onClick={() => onLikeButtonClick(tweet)} className="fa fa-thumbs-up">{like}</i>
              </button>
              <button className={""+(isDislike?"text-primary":"")}>
              <i onClick={onDisLikeButtonClick} className="fa fa-thumbs-down">{Dislike}</i>
              </button>

              {/* <button onClick={handleDislike}>Dislike ({dislikes})</button> */}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TweetComposer;
