import React, { useState } from 'react';
import EditUser from './EditUser'; // Import EditUser component

const UserList = () => {
  const [selectedUserId, setSelectedUserId] = useState(null); // State to store selected user ID

  // Function to handle edit button click
  const handleEdit = userId => {
    setSelectedUserId(userId); // Set the selected user ID
  };

  return (
    <div>
      <h1>User List</h1>
      {/* Assuming you have a list of users */}
      {users.map(user => (
        <div key={user.id}>
          <div>
            <strong>Username:</strong> {user.username}
          </div>
          <div>
            <strong>Email:</strong> {user.email}
          </div>
          {/* Edit button for each user */}
          <button onClick={() => handleEdit(user.id)}>Edit</button>
        </div>
      ))}
      {/* Pass selectedUserId to EditUser component */}
      {selectedUserId && <EditUser userId={selectedUserId} />}
    </div>
  );
};

export default UserList;
